import React, { useState, useEffect } from 'react';
import './App.css';

const API_URL = 'http://localhost:3001/cars';

function App() {
  const [cars, setCars] = useState([]); // Lista de autos
  const [newCar, setNewCar] = useState({
    marca: '',
    modelo: '',
    descripcion: '',
    valor: '',
    existencia: '',
    colores_disponibles: '',
  });
  const [editingCar, setEditingCar] = useState(null); // Auto que se está editando

  // Cargar la lista de autos al inicio
  useEffect(() => {
    fetchCars();
  }, []);

  const fetchCars = async () => {
    try {
      const response = await fetch(API_URL);
      const data = await response.json();
      setCars(data);
    } catch (error) {
      console.error('Error fetching cars:', error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    if (editingCar) {
      setEditingCar({ ...editingCar, [name]: value });
    } else {
      setNewCar({ ...newCar, [name]: value });
    }
  };

  const validateFields = (car) => {
    const { marca, modelo, descripcion, valor, existencia, colores_disponibles } = car;
    return marca && modelo && descripcion && valor && existencia && colores_disponibles;
  };

  const handleCreate = async () => {
    if (!validateFields(newCar)) {
      alert('Por favor, completa todos los campos.');
      return;
    }

    try {
      const response = await fetch(API_URL, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newCar),
      });

      if (response.ok) {
        fetchCars(); // Actualiza la lista después de agregar
        setNewCar({
          marca: '',
          modelo: '',
          descripcion: '',
          valor: '',
          existencia: '',
          colores_disponibles: '',
        }); // Limpia el formulario
      }
    } catch (error) {
      console.error('Error creating car:', error);
    }
  };

  const handleEdit = (car) => {
    setEditingCar(car);
  };

  const handleUpdate = async () => {
    if (!validateFields(editingCar)) {
      alert('Por favor, completa todos los campos.');
      return;
    }

    try {
      const response = await fetch(`${API_URL}/${editingCar.id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(editingCar),
      });

      if (response.ok) {
        fetchCars(); // Actualiza la lista después de editar
        setEditingCar(null); // Limpia el formulario de edición
      }
    } catch (error) {
      console.error('Error updating car:', error);
    }
  };

  const handleDelete = async (id) => {
    try {
      const response = await fetch(`${API_URL}/${id}`, {
        method: 'DELETE',
      });

      if (response.ok) {
        fetchCars(); // Actualiza la lista después de eliminar
      }
    } catch (error) {
      console.error('Error deleting car:', error);
    }
  };

  return (
    <div className="App">
      <h1>Autos en Venta</h1>

      {/* Formulario para agregar o actualizar autos */}
      <div className="form">
        <h2>{editingCar ? 'Actualizar Auto' : 'Agregar Auto'}</h2>
        <input
          type="text"
          name="marca"
          placeholder="Marca"
          value={editingCar ? editingCar.marca : newCar.marca}
          onChange={handleInputChange}
        />
        <input
          type="text"
          name="modelo"
          placeholder="Modelo"
          value={editingCar ? editingCar.modelo : newCar.modelo}
          onChange={handleInputChange}
        />
        <textarea
          name="descripcion"
          placeholder="Descripción"
          value={editingCar ? editingCar.descripcion : newCar.descripcion}
          onChange={handleInputChange}
        />
        <input
          type="number"
          name="valor"
          placeholder="Valor"
          value={editingCar ? editingCar.valor : newCar.valor}
          onChange={handleInputChange}
        />
        <input
          type="number"
          name="existencia"
          placeholder="Existencia"
          value={editingCar ? editingCar.existencia : newCar.existencia}
          onChange={handleInputChange}
        />
        <input
          type="text"
          name="colores_disponibles"
          placeholder="Colores disponibles (separados por comas)"
          value={editingCar ? editingCar.colores_disponibles : newCar.colores_disponibles}
          onChange={handleInputChange}
        />
        {editingCar ? (
          <button onClick={handleUpdate}>Actualizar Auto</button>
        ) : (
          <button onClick={handleCreate}>Agregar Auto</button>
        )}
      </div>

      {/* Lista de autos */}
      <div className="cars-list">
        <h2>Lista de Autos</h2>
        {cars.map((car) => (
          <div key={car.id} className="car-item">
            <h3>{car.marca} {car.modelo}</h3>
            <p>{car.descripcion}</p>
            <p><strong>Valor:</strong> ${car.valor}</p>
            <p><strong>Existencia:</strong> {car.existencia}</p>
            <p><strong>Colores:</strong> {car.colores_disponibles}</p>
            <button onClick={() => handleEdit(car)}>Editar</button>
            <button onClick={() => handleDelete(car.id)}>Eliminar</button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
