import React, { useState, useEffect } from "react";
import axios from "axios";
import "./App.css";

const App = () => {
  const [cars, setCars] = useState([]);
  const [search, setSearch] = useState("");
  const [form, setForm] = useState({ marca: "", modelo: "", descripcion: "", valor: "", colorDisponible: "" });
  const [editingCar, setEditingCar] = useState(null);
  const [isSubmitting, setIsSubmitting] = useState(false);

  useEffect(() => {
    fetchCars();
  }, []);

  const fetchCars = async () => {
    try {
      const response = await axios.get("http://localhost:3001/cars");
      setCars(response.data);
    } catch (error) {
      console.error("Error fetching cars:", error);
    }
  };

  const handleSearch = (e) => {
    setSearch(e.target.value);
  };

  const handleAddCar = async () => {
    if (isSubmitting) return;

    setIsSubmitting(true);
    try {
      if (editingCar) {
        await axios.put(`http://localhost:3001/cars/${editingCar.id}`, form);
        // Actualizar el estado local sin necesidad de recargar todos los datos
        setCars(cars.map(car => car.id === editingCar.id ? {...form, id: editingCar.id} : car));
      } else {
        const response = await axios.post("http://localhost:3001/cars", form);
        setCars([...cars, response.data]);
      }

      // Resetear el formulario solo después de una operación exitosa
      setForm({ marca: "", modelo: "", descripcion: "", valor: "", colorDisponible: "" });
      setEditingCar(null);
    } catch (error) {
      console.error("Error saving car:", error);
      // No resetear el formulario si hay un error
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleEditCar = (car) => {
    setForm(car);
    setEditingCar(car);
    // Hacer scroll suave al formulario para mejor experiencia de usuario
    document.querySelector('.form-container')?.scrollIntoView({ behavior: 'smooth' });
  };

  const handleDeleteCar = async (id) => {
    try {
      await axios.delete(`http://localhost:3001/cars/${id}`);
      // Actualizar el estado local sin necesidad de recargar todos los datos
      setCars(cars.filter(car => car.id !== id));
    } catch (error) {
      console.error("Error deleting car:", error);
    }
  };

  const filteredCars = cars.filter((car) =>
    car.marca.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div className="container">
      <h1>Gestión de Autos</h1>

      <div className="search-bar">
        <input
          type="text"
          placeholder="Buscar por marca..."
          value={search}
          onChange={handleSearch}
        />
      </div>

      <div className="form-container">
        <h2>{editingCar ? "Editar Auto" : "Agregar Nuevo Auto"}</h2>
        <input
          type="text"
          placeholder="Marca"
          value={form.marca}
          onChange={(e) => setForm({ ...form, marca: e.target.value })}
        />
        <input
          type="text"
          placeholder="Modelo"
          value={form.modelo}
          onChange={(e) => setForm({ ...form, modelo: e.target.value })}
        />
        <input
          type="text"
          placeholder="Descripción"
          value={form.descripcion}
          onChange={(e) => setForm({ ...form, descripcion: e.target.value })}
        />
        <input
          type="number"
          placeholder="Valor"
          value={form.valor}
          onChange={(e) => setForm({ ...form, valor: e.target.value })}
        />
        <input
          type="text"
          placeholder="Colores Disponibles"
          value={form.colorDisponible}
          onChange={(e) => setForm({ ...form, colorDisponible: e.target.value })}
        />
        <button onClick={handleAddCar} disabled={isSubmitting}>
          {editingCar ? (isSubmitting ? "Actualizando..." : "Actualizar") : (isSubmitting ? "Agregando..." : "Agregar")}
        </button>
        {editingCar && (
          <button 
            onClick={() => {
              setForm({ marca: "", modelo: "", descripcion: "", valor: "", colorDisponible: "" });
              setEditingCar(null);
            }}
            disabled={isSubmitting}
          >
            Cancelar
          </button>
        )}
      </div>

      <div className="car-list">
        {filteredCars.map((car) => (
          <div className="car-item" key={car.id}>
            <h3>{car.marca}</h3>
            <p>Modelo: {car.modelo}</p>
            <p>Descripción: {car.descripcion}</p>
            <p>Valor: ${car.valor}</p>
            <p>Colores: {car.colorDisponible}</p>
            <button onClick={() => handleEditCar(car)} disabled={isSubmitting}>
              Editar
            </button>
            <button onClick={() => handleDeleteCar(car.id)} disabled={isSubmitting}>
              Eliminar
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default App;
