import React, { useState, useEffect } from 'react';
import axios from 'axios';

const CarManager = () => {
  const [cars, setCars] = useState([]);
  const [editingId, setEditingId] = useState(null); // ID del auto que está siendo editado
  const [editedCar, setEditedCar] = useState({}); // Datos editados

  // Cargar los autos desde el backend
  const fetchCars = async () => {
    const response = await axios.get('http://localhost:3001/cars');
    setCars(response.data);
  };

  useEffect(() => {
    fetchCars();
  }, []);

  // Manejar clic en "Editar"
  const handleEdit = (id) => {
    const car = cars.find((car) => car.id === id);
    setEditingId(id);
    setEditedCar({ ...car }); // Clona el auto para edición
  };

  // Manejar cambios en los inputs
  const handleChange = (field, value) => {
    setEditedCar({ ...editedCar, [field]: value });
  };

  // Guardar cambios
  const handleSave = async (id) => {
    try {
      await axios.put(`http://localhost:3001/cars/${id}`, editedCar);
      fetchCars(); // Recarga la lista actualizada
      setEditingId(null); // Termina la edición
    } catch (error) {
      console.error('Error guardando los cambios:', error);
    }
  };

  // Manejar clic en "Cancelar"
  const handleCancel = () => {
    setEditingId(null); // Sale del modo edición
  };

  return (
    <div>
      <h1>Gestión de Autos</h1>
      <table>
        <thead>
          <tr>
            <th>Marca</th>
            <th>Modelo</th>
            <th>Descripción</th>
            <th>Valor</th>
            <th>Existencia</th>
            <th>Colores Disponibles</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {cars.map((car) => (
            <tr key={car.id}>
              {editingId === car.id ? (
                <>
                  <td>
                    <input
                      type="text"
                      value={editedCar.marca}
                      onChange={(e) => handleChange('marca', e.target.value)}
                    />
                  </td>
                  <td>
                    <input
                      type="text"
                      value={editedCar.modelo}
                      onChange={(e) => handleChange('modelo', e.target.value)}
                    />
                  </td>
                  <td>
                    <input
                      type="text"
                      value={editedCar.descripcion}
                      onChange={(e) => handleChange('descripcion', e.target.value)}
                    />
                  </td>
                  <td>
                    <input
                      type="number"
                      value={editedCar.valor}
                      onChange={(e) => handleChange('valor', e.target.value)}
                    />
                  </td>
                  <td>
                    <input
                      type="number"
                      value={editedCar.existencia}
                      onChange={(e) => handleChange('existencia', e.target.value)}
                    />
                  </td>
                  <td>
                    <input
                      type="text"
                      value={editedCar.colores_disponibles}
                      onChange={(e) => handleChange('colores_disponibles', e.target.value)}
                    />
                  </td>
                  <td>
                    <button onClick={() => handleSave(car.id)}>Guardar</button>
                    <button onClick={handleCancel}>Cancelar</button>
                  </td>
                </>
              ) : (
                <>
                  <td>{car.marca}</td>
                  <td>{car.modelo}</td>
                  <td>{car.descripcion}</td>
                  <td>{car.valor}</td>
                  <td>{car.existencia}</td>
                  <td>{car.colores_disponibles}</td>
                  <td>
                    <button onClick={() => handleEdit(car.id)}>Editar</button>
                  </td>
                </>
              )}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default CarManager;
